mapper.select:
SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_ID, MEMBER_NAME, MEMBER_NO, MEMBER_PHONE, MEMBER_EMAIL
FROM MEMBER
WHERE MEMBER_ID = #{memberId} AND MEMBER_PASSWORD = #{memberPassword}

SELECT MEMBER_EMAIL, MEMBER_PHONE, MEMBER_ID
FROM MEMBER
WHERE MEMBER_EMAIL = #{memberEmail} AND MEMBER_PHONE = #{memberPhone}

SELECT MEMBER_EMAIL, MEMBER_PHONE, MEMBER_ID, MEMBER_PASSWORD
FROM MEMBER
WHERE MEMBER_ID = #{memberId} AND MEMBER_EMAIL = #{memberEmail}
