mapper.select:
SELECT TL.RNUM, TL.ORDER_DATE, TL.ORDER_NO, TL.MEMBER_NO, TL.MEMBER_ID, TL.PRODUCT_NAME FROM( SELECT ROWNUM AS RNUM, OL.ORDER_DATE, OL.ORDER_NO, OL.MEMBER_NO, OL.PRODUCT_NO, OL.MEMBER_ID, OL.PRODUCT_NAME FROM ( SELECT L.ORDER_DATE, L.ORDER_NO, M.MEMBER_NO, P.PRODUCT_NO, M.MEMBER_ID, P.PRODUCT_NAME FROM ORDER_LIST L JOIN MEMBER M ON L.MEMBER_NO = M.MEMBER_NO JOIN PRODUCT P ON L.PRODUCT_NO = P.PRODUCT_NO ORDER BY ORDER_DATE DESC) OL
WHERE OL.MEMBER_NO = #{search}
WHERE OL.ORDER_NO = #{search}
WHERE OL.MEMBER_ID LIKE '%'||#{search}||'%'
WHERE OL.PRODUCT_NAME LIKE '%'||#{search}||'%'
) TL WHERE (TL.RNUM BETWEEN #{start} AND #{end}) ORDER BY TL.RNUM ASC

SELECT TL.RNUM, TL.ORDER_DATE, TL.ORDER_NO, TL.MEMBER_NO, TL.MEMBER_ID, TL.PRODUCT_NAME FROM( SELECT ROWNUM AS RNUM, OL.ORDER_DATE, OL.ORDER_NO, OL.MEMBER_NO, OL.PRODUCT_NO, OL.MEMBER_ID, OL.PRODUCT_NAME FROM ( SELECT L.ORDER_DATE, L.ORDER_NO, M.MEMBER_NO, P.PRODUCT_NO, M.MEMBER_ID, P.PRODUCT_NAME FROM ORDER_LIST L JOIN MEMBER M ON L.MEMBER_NO = M.MEMBER_NO JOIN PRODUCT P ON L.PRODUCT_NO = P.PRODUCT_NO WHERE M.MEMBER_NO = #{sessionMemberNo} ORDER BY ORDER_DATE DESC) OL
WHERE OL.ORDER_NO = #{search}
WHERE OL.PRODUCT_NAME LIKE '%'||#{search}||'%'
) TL WHERE (TL.RNUM BETWEEN #{start} AND #{end}) ORDER BY TL.RNUM ASC

SELECT COUNT(*) FROM ORDER_LIST OL JOIN MEMBER M ON OL.MEMBER_NO = M.MEMBER_NO JOIN PRODUCT P ON OL.PRODUCT_NO = P.PRODUCT_NO
WHERE M.MEMBER_NO = #{search}
WHERE OL.ORDER_NO = #{search}
WHERE M.MEMBER_ID LIKE '%'||#{search}||'%'
WHERE P.PRODUCT_NAME LIKE '%'||#{search}||'%'

SELECT COUNT(*) FROM ORDER_LIST OL JOIN MEMBER M ON OL.MEMBER_NO = M.MEMBER_NO JOIN PRODUCT P ON OL.PRODUCT_NO = P.PRODUCT_NO WHERE M.MEMBER_NO = #{sessionMemberNo}
AND OL.ORDER_NO = #{search}
AND P.PRODUCT_NAME LIKE '%'||#{search}||'%'

mapper.update:
UPDATE ORDER_LIST
SET ORDER_STATUS = '취소'
WHERE ORDER_NO = #{orderNo}

UPDATE ORDER_LIST
SET ORDER_STATUS = '확정'
WHERE ORDER_NO = #{orderNo}

mapper.select:
SELECT TL.RNUM, TL.ORDER_CANCEL_DATE, TL.ORDER_NO, TL.MEMBER_NO, TL.MEMBER_ID, TL.PRODUCT_NAME FROM( SELECT ROWNUM AS RNUM, OL.ORDER_CANCEL_DATE, OL.ORDER_NO, OL.MEMBER_NO, OL.PRODUCT_NO, OL.MEMBER_ID, OL.PRODUCT_NAME FROM ( SELECT OC.ORDER_CANCEL_DATE, L.ORDER_NO, M.MEMBER_NO, P.PRODUCT_NO, M.MEMBER_ID, P.PRODUCT_NAME FROM ORDER_LIST L JOIN MEMBER M ON L.MEMBER_NO = M.MEMBER_NO JOIN PRODUCT P ON L.PRODUCT_NO = P.PRODUCT_NO JOIN ORDER_CANCEL OC ON L.ORDER_NO = OC.ORDER_NO ORDER BY ORDER_DATE DESC) OL
WHERE OL.MEMBER_NO = #{search}
WHERE OL.ORDER_NO = #{search}
WHERE OL.MEMBER_ID LIKE '%'||#{search}||'%'
WHERE OL.PRODUCT_NAME LIKE '%'||#{search}||'%'
) TL WHERE (TL.RNUM BETWEEN #{start} AND #{end}) ORDER BY TL.RNUM ASC

